pragma solidity 0.8.15;

import "../lib/struct/EnumerableSet.sol";
import "../lib/struct/EnumerableMap.sol";
import "../proxy/BaseUUPSStorage.sol";

abstract contract AccessControlStorage is BaseUUPSStorage {

    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using EnumerableMap for EnumerableMap.Bytes32ToBytes32Map;
    using EnumerableMap for EnumerableMap.AddressToUintMap;

    bytes32 public constant LIVELY_GENERAL_REALM = keccak256(abi.encodePacked("LIVELY_GENERAL_REALM"));
    bytes32 public constant LIVELY_GENERAL_GROUP = keccak256(abi.encodePacked("LIVELY_GENERAL_GROUP"));
    bytes32 public constant LIVELY_ADMIN_ROLE = keccak256(abi.encodePacked("LIVELY_ADMIN_ROLE"));
    bytes32 public constant LIVELY_SYSTEM_ADMIN_ROLE = keccak256(abi.encodePacked("LIVELY_SYSTEM_ADMIN_ROLE"));
    bytes32 public constant LIVELY_PUBLIC_ROLE = keccak256(abi.encodePacked("LIVELY_ADMIN_ROLE"));


    enum Status {
        NONE,
        ENABLED,
        DISABLED
    }

    struct Context {
        bytes32 realm;
        address smca;                    // smart contract address
        mapping(bytes32 => Status) resources;      // function selector + Role => Status
        EnumerableSet.Bytes32Set funcSet; 
    }

    struct Role {
        string name;
        bytes32 group;
        bool isEnabled;
        EnumerableMap.AddressToUintMap userMap;
        // mapping(address => Status) userMap;        
        // EnumerableSet.AddressSet userSet;
        // EnumerableSet.Bytes32Set ctxSet;        // TODO check it if needed or no
    }

    struct Realm {
        string name;
        bool isEnabled;
        bool isUpgradable;
        EnumerableSet.Bytes32Set ctxSet;        
    }

    struct Group {
        string name;
        bool isEnabled;
        EnumerableSet.Bytes32Set roles;       
    }


    struct DataMaps {
        mapping(address => bytes32[]) userRoleMap;
        mapping(bytes32 => Context)  ctxMap;
        mapping(bytes32 => Role)  roleMap;
        mapping(bytes32 => Realm) realmMap;
        mapping(bytes32 => Group) groupMap;
    }


    // mapping(address => bytes32[]) internal _userRoleMap;
    // mapping(bytes32 => Context) internal _ctxMap;
    // mapping(bytes32 => Role) internal _roleMap;
    // mapping(bytes32 => Realm) internal _realmMap;
    // mapping(bytes32 => Group) internal _groupMap;

    DataMaps internal _dataMaps;

    Realm internal _livelyGeneralRealm;
    Group internal _livelyGeneralGroup;
    Role  internal _livelyAdminRole;
    Role  internal _liveltSystemAdminRole;
}","keccak256":"0xab7535255ff7fff6d7199669c493fece4d3f06a3b3a60b09d6d64260be50b605","license":"MIT"},"src/contracts/acl/IContextManagement.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

interface IContextManagement {

    struct RequestContext {
        bytes32 realm;
        address smca;                    
    }

    struct RequestContextResource {
        bytes32 role;
        bytes4[] funcSelectors;
    }

    event ContextRegistered(bytes32 indexed context, address indexed scma, address indexed sender, bytes32 realm);

    event ContextUpdated(bytes32 indexed context, address indexed scma, address indexed sender);

    event ContextRoleGranted(bytes32 indexed context, bytes32 indexed role, 
                            address indexed sender, bytes4 functionSelector, bytes32 realm);

    event ContextRoleRevoked(bytes32 indexed context, bytes32 indexed role, 
                            address indexed sender, bytes4 functionSelector, bytes32 realm);

    function registerContext(RequestContext calldata rc, RequestContextResource[] calldata rcr) external returns (bytes32);

    function updateContext(RequestContext calldata rc, RequestContextResource[] calldata rcr) external returns (bytes32);

    function grantContextRole(bytes32 ctx, bytes4 functionSelector, bytes32 role) external returns (bool);

    function revokeContextRole(bytes32 ctx, bytes4 functionSelector, bytes32 role) external returns (bool);

    function enableContext(bytes32 ctx) external returns (bool);

    function disableContext(bytes32 ctx) external returns (bool);

    function enableUpgradeContext(bytes32 ctx) external returns (bool);

    function hasContextRole(bytes32 ctx, bytes32 role, bytes4 functionSelector) external view returns (bool);

    function getContext(bytes32 ctx) external view returns (string memory, string memory, bytes32, bool);

    function getContextFuncs(bytes32 ctx) external view returns (bytes4[] memory);
}","keccak256":"0x832dae2f1c46783dc1bede35ad8f514b06c43faba2930fd9c1409c66c2da9fe7","license":"MIT"},"src/contracts/lib/Address.sol":{"content":"// SPDX-License-Identifier: MIT

