pragma solidity 0.8.15;

import "../../acl/IContextManagement.sol";
import "../../acl/AccessControlStorage.sol";
import "../struct/EnumerableSet.sol";

library ContextManagementLib {

    using EnumerableSet for EnumerableSet.Bytes32Set;

    function registerContext(AccessControlStorage.DataMaps storage data, 
                             IContextManagement.RequestContext calldata rc, 
                             IContextManagement.RequestContextResource[] calldata rcr) external returns (bytes32) {
        require(msg.sender == rc.smca, "Illegal Contract Address");
        require(bytes(data.realmMap[rc.realm].name).length != 0, "Realm Not Found");

        bytes32 ctx = ContextUtils.generateCtx(rc.smca);
        require(data.ctxMap[ctx].smca == address(0), "Context Already Registered");
        AccessControlStorage.Context storage newContext = data.ctxMap[ctx];
        newContext.realm = rc.realm;
        newContext.smca = rc.smca;

        for (uint256 i = 0; i < rcr.length; i++) {
            require(bytes(data.roleMap[rcr[i].role].name).length != 0, "Role Not Found");
            for (uint256 j = 0; j < rcr[i].funcSelectors.length; j++) {
                bytes32 selectorKey = keccak256(abi.encodePacked(rcr[i].funcSelectors[j], rcr[i].role));
                newContext.resources[selectorKey] = AccessControlStorage.Status.ENABLED;
                newContext.funcSet.add(rcr[i].funcSelectors[j]); 
            }
        }

        return ctx;
    }

