  function setRealmUpgradeStatus(bytes32 realm, bool status) external returns (bool);

  function hasRealmContext(bytes32 realm, bytes32 context) external view returns (bool);

  function getRealmInfo(bytes32 realm)
    external
    view
    returns (
      string memory,
      bool,
      bool
