// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "../../token/asset/IAssetManagerERC20.sol";
import "../../token/asset/AssetManagerStorageERC20.sol";
import "../../token/asset/ERC20/AssetERC20.sol";
import "../../token/lively/IERC20.sol";
import "../../proxy/IProxy.sol";
import "../../acl/IContextManagement.sol";
import "../../acl/IGroupManagement.sol";
import "../../acl/IRoleManagement.sol";
import "../../acl/IAccessControl.sol";
import "../../utils/IERC165.sol";
import "../struct/LEnumerableSet.sol";
import "../LContextUtils.sol";
import "../proxy/LClones.sol";

library LAssetManagerERC20 {
  using LEnumerableSet for LEnumerableSet.AddressSet;
  using LClones for address;

  bytes32 public constant LIB_NAME = keccak256(abi.encodePacked("LAssetManagerERC20"));
  bytes32 public constant LIB_VERSION = keccak256(abi.encodePacked("1.0.0"));

  bytes32 public constant LIVELY_ASSET_GROUP = keccak256(abi.encodePacked("LIVELY_ASSET_GROUP"));
  bytes32 public constant LIVELY_ADMIN_ROLE = keccak256(abi.encodePacked("LIVELY_ADMIN_ROLE"));
  bytes32 public constant LIVELY_SYSTEM_ADMIN_ROLE = keccak256(abi.encodePacked("LIVELY_SYSTEM_ADMIN_ROLE"));
  bytes32 public constant LIVELY_ASSET_ADMIN_ROLE = keccak256(abi.encodePacked("LIVELY_ASSET_ADMIN_ROLE"));
  bytes32 public constant LIVELY_ASSET_MANAGER_ROLE = keccak256(abi.encodePacked("LIVELY_ASSET_MANAGER_ROLE"));

  function createRequestContext(
    bytes32 domainName,
    bytes32 domainVersion,
    bytes32 realm
  )
    external
    view
    returns (IContextManagement.RequestContext memory, IContextManagement.RequestRegisterContext[] memory)
  {
    IContextManagement.RequestRegisterContext[] memory rrc = new IContextManagement.RequestRegisterContext[](3);

    rrc[0].role = LIVELY_ADMIN_ROLE;
    rrc[0].isEnabled = true;
    rrc[0].funcSelectors = new bytes4[](2);
    rrc[0].funcSelectors[0] = IProxy.setUpgradeStatus.selector;
    rrc[0].funcSelectors[1] = IProxy.setSafeMode.selector;

    rrc[1].role = LIVELY_SYSTEM_ADMIN_ROLE;
    rrc[1].isEnabled = true;
    rrc[1].funcSelectors = new bytes4[](2);
    rrc[1].funcSelectors[0] = IProxy.setLocalAdmin.selector;
    rrc[1].funcSelectors[1] = IProxy.upgradeTo.selector;

    rrc[2].role = LIVELY_ASSET_ADMIN_ROLE;
    rrc[2].isEnabled = true;
    rrc[2].funcSelectors = new bytes4[](17);
    rrc[2].funcSelectors[0] = IAssetManagerERC20.tokenLock.selector;
    rrc[2].funcSelectors[1] = IAssetManagerERC20.tokenBatchLock.selector;
    rrc[2].funcSelectors[2] = IAssetManagerERC20.tokenTransfer.selector;
    rrc[2].funcSelectors[3] = IAssetManagerERC20.tokenBatchTransfer.selector;
    rrc[2].funcSelectors[4] = IAssetManagerERC20.tokenTransferFrom.selector;
    rrc[2].funcSelectors[5] = IAssetManagerERC20.tokenBatchTransferFrom.selector;
    rrc[2].funcSelectors[6] = IAssetManagerERC20.tokenApprove.selector;
    rrc[2].funcSelectors[7] = IAssetManagerERC20.tokenIncreaseAllowance.selector;
    rrc[2].funcSelectors[8] = IAssetManagerERC20.tokenDecreaseAllowance.selector;
    rrc[2].funcSelectors[9] =  IAssetManagerERC20.createAsset.selector;
    rrc[2].funcSelectors[10] = IAssetManagerERC20.updateAssetSubject.selector;
    rrc[2].funcSelectors[11] = IAssetManagerERC20.registerToken.selector;
    rrc[2].funcSelectors[12] = IAssetManagerERC20.registerAsset.selector;
    rrc[2].funcSelectors[13] = IAssetManagerERC20.removeAsset.selector;
    rrc[2].funcSelectors[14] = IAssetManagerERC20.setSafeModeToken.selector;
    rrc[2].funcSelectors[15] = bytes4(keccak256("livelyTokensDistribution(address)"));
    rrc[2].funcSelectors[16] = bytes4(keccak256("withdrawBalance(address)"));

    IContextManagement.RequestContext memory rc = IContextManagement.RequestContext({
      name: domainName,
      version: domainVersion,
      realm: realm,
      contractId: address(this),
      status: true
    });

    return (rc, rrc);
  }

  function createAsset(AssetManagerStorageERC20.DataCollection storage data, IAssetManagerERC20.CreateAssetRequest calldata request, address accessControlManager, address assetSubject, bytes calldata assetCreationSignature) external returns (address, address) {
    require(bytes(request.assetName).length > 0 , "Invalid Asset Name");
    require(bytes(request.assetVersion).length > 0 , "Invalid Asset Version");
    require(request.tokenId != address(0) , "Invalid TokenId Address");
    require(assetSubject != address(0), "Invalid Asset Subject ERC20");
    require(data.tokensSet.contains(request.tokenId), "TokenId Not Found");

    bytes32 assetGroup = LIVELY_ASSET_GROUP;
    require(IGroupManagement(accessControlManager).hasGroupRole(assetGroup, request.role), "Asset Role Not Found");

    AssetManagerStorageERC20.TokenData storage tokenData = data.tokens[request.tokenId];
    require(!tokenData.assets.contains(assetSubject.predictDeterministicAddress(request.salt)), "AssetId Already Exists");

    AssetERC20.InitRequest memory initRequest = AssetERC20.InitRequest ({
      domainName: request.assetName,
      domainVersion: request.assetVersion,  
      domainRealm: IProxy(address(this)).contractRealm(),
      erc20Token: request.tokenId,
      accessControl: accessControlManager,
      assetManager: address(this),
      assetRole: request.role,
      salt: request.salt,
      subject: assetSubject,
      signature: assetCreationSignature
    });

    address newAsset = assetSubject.cloneDeterministic(request.salt);
    AssetERC20(payable(newAsset)).initialize(initRequest);
    tokenData.assets.add(newAsset);
    return (newAsset, assetSubject);
  }

  function registerToken(AssetManagerStorageERC20.DataCollection storage data, address tokenId) external returns (string memory, string memory) {   
    require(!data.tokensSet.contains(tokenId), "TokenId Already Registered");

    try IERC165(tokenId).supportsInterface(type(IERC20).interfaceId) returns (bool isSupported) {
      require(isSupported, "Invalid ERC20");
    } catch {
      revert("Illegal ERC20");
    }

    try IERC165(tokenId).supportsInterface(type(IERC20Extra).interfaceId) returns (bool isSupported) {
      require(isSupported, "Invalid ERC20Extra");
    } catch {
      revert("Illegal ERC20Extra");
    }

    try IERC165(tokenId).supportsInterface(type(IERC20Lock).interfaceId) returns (bool isSupported) {
      require(isSupported, "Invalid ERC20Lock");
    } catch {
      revert("Illegal ERC20Lock");
    }

    string memory tokenName = IERC20(tokenId).name();
    string memory tokenSymbol = IERC20(tokenId).symbol();

    data.tokensSet.add(tokenId);
    AssetManagerStorageERC20.TokenData storage tokenData = data.tokens[tokenId];
    tokenData.status = IAssetEntity.Status.ACTIVE;
    return (tokenName, tokenSymbol);
  }

  function registerAsset(AssetManagerStorageERC20.DataCollection storage data, address assetId) external returns (bool, address) {
    require(assetId != address(0), "Invalid AssetId Address");

    try IERC165(assetId).supportsInterface(type(IAssetERC20).interfaceId) returns (bool isSupported) {
      require(isSupported, "Invalid IAssetERC20");
    } catch {
      revert("Illegal IAssetERC20");
    }

    try IERC165(assetId).supportsInterface(type(IAssetEntity).interfaceId) returns (bool isSupported) {
      require(isSupported, "Invalid IAssetEntity");
    } catch {
      revert("Illegal IAssetEntity");
    }


    address tokenId = IAssetEntity(assetId).assetToken();
    require(data.tokensSet.contains(tokenId), "TokenId Not Found");

    AssetManagerStorageERC20.TokenData storage tokenData = data.tokens[tokenId];
    require(!tokenData.assets.contains(assetId), "AssetId Already Registered");

    tokenData.assets.add(assetId);
    if(IAssetEntity(assetId).assetSafeMode()) {
      IAssetEntity(assetId).assetSafeModeSet(false);
    }
    return (true, tokenId);
  }

  function removeAsset(AssetManagerStorageERC20.DataCollection storage data, address assetId) external returns (address) {
    require(assetId != address(0), "Invalid AssetId Address");

    address tokenId = IAssetEntity(assetId).assetToken();
    require(data.tokensSet.contains(tokenId), "TokenId Not Found");

    AssetManagerStorageERC20.TokenData storage tokenData = data.tokens[tokenId];
    require(tokenData.assets.contains(assetId), "AssetId Not Found");

    tokenData.assets.remove(assetId);
    if(!IAssetEntity(assetId).assetSafeMode()) {
      IAssetEntity(assetId).assetSafeModeSet(true);
    }    
    return tokenId;
  }

  function setSafeModeToken(AssetManagerStorageERC20.DataCollection storage data, address tokenId, bool isEnabled) external returns (bool) {    
    require(data.tokensSet.contains(tokenId), "TokenId Not Found");
    AssetManagerStorageERC20.TokenData storage tokenData = data.tokens[tokenId];
    for(uint i = 0; i < tokenData.assets.length(); i++) {
      IAssetEntity(tokenData.assets.at(i)).assetSafeModeSet(isEnabled);
    }

    tokenData.status = IAssetEntity.Status.SAFE_MODE;  
    return true;
  }

  function predictAddress(address implementation, bytes32 salt, address deployer) external pure returns (address) {
    return implementation.predictDeterministicAddress(salt, deployer);
  }
}

src/contracts/token/asset/AssetManagerER.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "./IAssetManagerERC20.sol";
import "./IAssetEntity.sol";
import "./AssetManagerStorageERC20.sol";
import "./ERC20/IAssetERC20.sol";
import "../lively/IERC20.sol";
import "../lively/LivelyToken.sol";
import "../../proxy/BaseUUPSProxy.sol";
import "../../lib/token/LAssetManagerERC20.sol";

contract AssetManagerERC20 is AssetManagerStorageERC20, BaseUUPSProxy, IAssetManagerERC20 {

  using LEnumerableSet for LEnumerableSet.AddressSet;

  struct InitRequest {
    string domainName;
    string domainVersion;
    string domainRealm;    
    address accessControlManager;
    bytes assetManagerSignature;
  }

  constructor() {}

  function initialize(InitRequest calldata request) public onlyProxy onlyLocalAdmin initializer {

    bytes32 realm = keccak256(abi.encodePacked(request.domainRealm));
    __BASE_UUPS_init(request.domainName, request.domainVersion, realm, request.accessControlManager);

    (IContextManagement.RequestContext memory rc, IContextManagement.RequestRegisterContext[] memory rrc) = 
      LAssetManagerERC20.createRequestContext(_domainName, _domainVersion, _domainRealm);

    IContextManagement(_accessControlManager).registerContext(request.assetManagerSignature, rc, rrc);

    emit Initialized(
      _msgSender(),
      address(this),
      _implementation(),
      request.domainName,
      request.domainVersion,
      realm,
      _getInitializedCount()
    );
  }

  function livelyTokensDistribution(address tokenId) public returns (bool) {
    _policyInterceptor(this.livelyTokensDistribution.selector);
    require(_data.tokensSet.contains(tokenId), "TokenId Not Found");

    TokenData storage tokenData = _data.tokens[tokenId];
    require(tokenData.assets.length() == 7, "Asset Required Failed");

    address[7] memory assets;
    for(uint i = 0; i < 7; i++) {
      assets[i] = tokenData.assets.at(i);
    }
    return LivelyToken(payable(tokenId)).tokensDistribution(address(this), assets);
  }

   /**
   * @dev See {IERC165-supportsInterface}.
   */
  function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
    return interfaceId == type(IAssetManagerERC20).interfaceId || super.supportsInterface(interfaceId);
  }

  function tokenLock(address assetId, IERC20Lock.LockTokenRequest calldata lockRequest) external returns (bytes32) {
    _validationAndPolicyInterceptor(assetId, this.tokenLock.selector);
    return IAssetERC20(assetId).tokenLock(lockRequest);
  }

  function tokenBatchLock(address assetId, IERC20Lock.LockTokenRequest[] calldata lockRequests) external returns (bytes32[] memory) {
    _validationAndPolicyInterceptor(assetId, this.tokenBatchLock.selector);
    return IAssetERC20(assetId).tokenBatchLock(lockRequests);
  }

  function tokenTransfer(address assetId, address to, uint256 amount) external returns (bool) {
    _validationAndPolicyInterceptor(assetId, this.tokenTransfer.selector);
    return IAssetERC20(assetId).tokenTransfer(to, amount);
  }

  function tokenBatchTransfer(address assetId, IERC20Extra.BatchTransferRequest[] calldata request) external returns (bool) {
    _validationAndPolicyInterceptor(assetId, this.tokenBatchTransfer.selector);
    return IAssetERC20(assetId).tokenBatchTransfer(request);
  }

  function tokenTransferFrom(address assetId, address from, address to, uint256 amount) external returns (bool) {
    _validationAndPolicyInterceptor(assetId, this.tokenTransferFrom.selector);
    return IAssetERC20(assetId).tokenTransferFrom(from, to, amount);
  }

  function tokenBatchTransferFrom(address assetId, IERC20Extra.BatchTransferFromRequest[] calldata request) external returns (bool) {
    _validationAndPolicyInterceptor(assetId, this.tokenBatchTransferFrom.selector);
    return IAssetERC20(assetId).tokenBatchTransferFrom(request);
  }

  function tokenApprove(address assetId, address spender, uint256 amount) external returns (bool) {
    _validationAndPolicyInterceptor(assetId, this.tokenApprove.selector);
    return IAssetERC20(assetId).tokenApprove(spender, amount);
  }

  function tokenIncreaseAllowance(address assetId, address spender, uint256 amount) external returns (uint256) {
    _validationAndPolicyInterceptor(assetId, this.tokenIncreaseAllowance.selector);
    return IAssetERC20(assetId).tokenIncreaseAllowance(spender, amount);
  }

  function tokenDecreaseAllowance(address assetId, address spender, uint256 amount) external returns (uint256) {
    _validationAndPolicyInterceptor(assetId, this.tokenDecreaseAllowance.selector);
    return IAssetERC20(assetId).tokenDecreaseAllowance(spender, amount);
  }

  function createAsset(CreateAssetRequest calldata request) external returns (address) {
    _policyInterceptor(this.createAsset.selector);
    (address newAsset, address assetSubject) = LAssetManagerERC20.createAsset(_data, request, _accessControlManager, _assetSubjectERC20, _assetCreationSignature);
    emit AssetCreated(_msgSender(), newAsset, request.tokenId, assetSubject);
    return newAsset;
  }

  function registerToken(address tokenId) external returns (bool) {
    _policyInterceptor(this.registerToken.selector);
    (string memory tokenName, string memory tokenSymbol) = LAssetManagerERC20.registerToken(_data, tokenId);
    emit TokenRegistered(_msgSender(), tokenId, tokenName, tokenSymbol);
    return true;
  }

  function registerAsset(address assetId) external returns (bool) {
    _policyInterceptor(this.registerAsset.selector);
    (bool result, address tokenId) = LAssetManagerERC20.registerAsset(_data, assetId);
    emit AssetRegistered(_msgSender(), assetId, tokenId);
    return result;
  }

  function removeAsset(address assetId) external returns (bool) { 
    _policyInterceptor(this.removeAsset.selector);
    address tokenId = LAssetManagerERC20.removeAsset(_data, assetId);
    emit AssetRemoved(_msgSender(), assetId, tokenId);
    return true;
  }

  function updateAssetSubject(address assetSubject, bytes calldata assetCreationSignature) external returns (bool) {
    _policyInterceptor(this.updateAssetSubject.selector);
    try IERC165(assetSubject).supportsInterface(type(IAssetERC20).interfaceId) returns (bool isSupported) {
      require(isSupported, "Invalid IAssetERC20");
    } catch {
      revert("Illegal IAssetERC20");
    }

    try IERC165(assetSubject).supportsInterface(type(IAssetEntity).interfaceId) returns (bool isSupported) {
      require(isSupported, "Invalid IAssetEntity");
    } catch {
      revert("Illegal IAssetEntity");
    }

    require(_assetSubjectERC20 != assetSubject, "AssetSubject Already Exists");
    require(assetCreationSignature.length > 0, "Invalid Signature");
    _assetSubjectERC20 = assetSubject;
    _assetCreationSignature = assetCreationSignature;
    emit AssetSubjectUpdated(_msgSender(), assetSubject);
    return true;
  }

  function setSafeModeToken(address tokenId, bool isEnabled) external returns (bool) {
    _policyInterceptor(this.setSafeModeToken.selector);
    emit TokenSafeModeChanged(_msgSender(), tokenId, isEnabled);
    return LAssetManagerERC20.setSafeModeToken(_data, tokenId, isEnabled);
  }

  function getAllTokens() external view returns(address[] memory) {
    return _data.tokensSet.values();
  }

  function getTokenInfo(address tokenId) external view returns (IAssetEntity.Status, address[] memory) {
   TokenData storage tokenData = _data.tokens[tokenId];
    return (tokenData.status, tokenData.assets.values());
  }

  function isSafeModeAsset(address assetId) external view returns (bool) {
    return IAssetEntity(assetId).assetSafeMode();
  }

  function isAssetExists(address assetId) external view returns (bool) {
    return _data.tokens[IAssetEntity(assetId).assetToken()].assets.contains(assetId);
  }

  function isTokenExists(address tokenId) external view returns (bool) {
    return _data.tokensSet.contains(tokenId);
  }

  function predictAddress(address implementation, bytes32 salt, address deployer) external pure returns (address) {
    return LAssetManagerERC20.predictAddress(implementation, salt, deployer);
  }

  function getAssetSubject() external view returns (address) {
    return _assetSubjectERC20;
  }

  function getLibrary() public pure returns(address) {
    return address(LAssetManagerERC20);
  }

  function _policyInterceptor(bytes4 funcSelector) private safeModeCheck aclCheck(funcSelector) {}

  function _validationAndPolicyInterceptor(address assetId, bytes4 funcSelector) private {
    _policyInterceptor(funcSelector);
    require(assetId != address(0), "Invalid AssetId");

    try IERC165(assetId).supportsInterface(type(IAssetEntity).interfaceId) returns (bool isSupported) {
      require(isSupported, "Invalid IAssetEntity");
    } catch {
      revert("Illegal IAssetEntity");
    }

    address tokenId = IAssetEntity(assetId).assetToken();
    require(_data.tokensSet.contains(tokenId), "TokenId Not Found");

    TokenData storage tokenData = _data.tokens[tokenId];    
    require(tokenData.assets.contains(assetId), "AssetId Not Found");
  }
}

