    _policyInterceptor(this.tokenBatchTransferFrom.selector);
    emit AssetERC20Called(_msgSender(), address(this), this.tokenBatchTransferFrom.selector);
    return IERC20Extra(_erc20TokenId).batchTransferFrom(request);
