    bytes32 domainId;
    uint32 contextLimit;
    string name;
    LEnumerableSet.Bytes32Set contexts;
  }

  struct DomainEntity {
    BaseScope bs;
    bytes32 universeId;
    uint16 realmLimit;
    string name;
    LEnumerableSet.Bytes32Set realms;
  }

  struct UniverseEntity {
