  }

  struct UpdateAlterabilityRequest {
    bytes32 id;
    AlterabilityStatus alstat;
  }

  struct UpdateAdminRequest {
    bytes32 id;
    bytes32 adminId;
  }

  // used in Policy / Role / Type
  struct UpdateScopeRequest {
    bytes32 id;
    bytes32 scopeId;
  }

  ////////////////////////////////////////////////////////////////////
  // Profiles

  struct ProfileMemberSignature {
    string profileName;
