  function profileGetRoleAdmin(
    IACLCommons.ProfileEntity storage profileEntity,
    IACLCommons.ScopeType requestScopeType,
    bytes32 requestScopeAdmin,
    bytes32 scopeId,
    bytes32 adminId,
    bytes32 profileId
  ) external view returns (bytes32 roleAdminId) {
