    REALM_ACTIVITY_FORBIDDEN,
    DOMAIN_ACTIVITY_FORBIDDEN,
    UNIVERSE_ACTIVITY_FORBIDDEN
  }

  error ACLUnauthorized();
  error ACLPolicyForbidden();
  error ACLCallForbidden();
